// UserTable.js
import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { deleteUser, editUser } from '../store/UserSlice';
import '../styles/UserTable.css'; // Importing the styles file

const UserTable = () => {
  const users = useSelector((state) => state.users);
  const dispatch = useDispatch();
  const [editMode, setEditMode] = useState(false);
  const [editedUser, setEditedUser] = useState(null);
  const [editedFirstName, setEditedFirstName] = useState('');
  const [editedLastName, setEditedLastName] = useState('');
  const [editedAge, setEditedAge] = useState('');
  const [editedCnic, setEditedCnic] = useState('');
  const [errors, setErrors] = useState({});

  const handleDelete = (id) => {
    dispatch(deleteUser(id));
  };

  const handleEdit = (user) => {
    setEditedUser(user);
    setEditedFirstName(user.firstName);
    setEditedLastName(user.lastName);
    setEditedAge(user.age);
    setEditedCnic(user.cnic);
    setEditMode(true);
  };

  const handleSaveEdit = () => {
    const validationErrors = validateForm();
    if (Object.keys(validationErrors).length === 0) {
      dispatch(editUser({ id: editedUser.id, updatedUser: { firstName: editedFirstName, lastName: editedLastName, age: editedAge, cnic: editedCnic } }));
      setEditMode(false);
    } else {
      setErrors(validationErrors);
    }
  };

  const validateForm = () => {
    let errors = {};
    if (!editedFirstName) {
      errors.firstName = 'First name is required';
    }
    if (!editedLastName) {
      errors.lastName = 'Last name is required';
    }
    if (!editedAge) {
      errors.age = 'Age is required';
    }
    if (!editedCnic) {
      errors.cnic = 'CNIC is required';
    }
    return errors;
  };

  return (
    <div className="user-table-container">
      <table className="user-table">
        <thead>
          <tr>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Age</th>
            <th>CNIC</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users?.users?.map((user) => (
            <tr key={user.id}>
              <td>{user.firstName}</td>
              <td>{user.lastName}</td>
              <td>{user.age}</td>
              <td>{user.cnic}</td>
              <td className='ED'>
                <button onClick={() => handleEdit(user)}>Edit</button>
                <button onClick={() => handleDelete(user.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {editMode && (
        <div className="modal" style={{ display: 'block' }}>
          <div className="modal-content" style={{ width: '400px', margin: 'auto', padding: '20px', backgroundColor: '#fefefe', border: '1px solid #888' }}>
            <span className="close" onClick={() => setEditMode(false)} style={{ color: '#aaa', float: 'right', fontSize: '28px', fontWeight: 'bold', cursor: 'pointer' }}>&times;</span>
            <h2>Edit User</h2>
            <form onSubmit={(e) => { e.preventDefault(); handleSaveEdit(); }}>
              <div>
                <label htmlFor="firstName">First Name:</label>
                <input type="text" id="firstName" value={editedFirstName} onChange={(e) => setEditedFirstName(e.target.value)} />
                {errors.firstName && <div className="error">{errors.firstName}</div>}
              </div>
              <div>
                <label htmlFor="lastName">Last Name:</label>
                <input type="text" id="lastName" value={editedLastName} onChange={(e) => setEditedLastName(e.target.value)} />
                {errors.lastName && <div className="error">{errors.lastName}</div>}
              </div>
              <div>
                <label htmlFor="age">Age:</label>
                <input type="number" id="age" value={editedAge} onChange={(e) => setEditedAge(e.target.value)} />
                {errors.age && <div className="error">{errors.age}</div>}
              </div>
              <div>
                <label htmlFor="cnic">CNIC:</label>
                <input type="text" id="cnic" value={editedCnic} onChange={(e) => setEditedCnic(e.target.value)} />
                {errors.cnic && <div className="error">{errors.cnic}</div>}
              </div>
              <button type="submit">Save</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserTable;
