// UserTable.js
import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { deleteUser, editUser } from '../store/UserSlice';

const UserTable = () => {
  const users = useSelector((state) => state.users);
  const dispatch = useDispatch();
  const [editMode, setEditMode] = useState(false);
  const [editedUser, setEditedUser] = useState(null);

  const handleDelete = (id) => {
    dispatch(deleteUser(id));
  };

  const handleEdit = (user) => {
    setEditedUser(user);
    setEditMode(true);
  };

  const handleSaveEdit = (editedData) => {
    dispatch(editUser({ id: editedUser.id, updatedUser: editedData }));
    setEditedUser(null);
    setEditMode(false);
  };

  return (
    <div>
      <h2>USERS LIST</h2>
      <table>
        <thead>
          <tr>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Age</th>
            <th>CNIC</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users?.users?.map((user) => (
            <tr key={user.id}>
              <td>{user.firstName}</td>
              <td>{user.lastName}</td>
              <td>{user.age}</td>
              <td>{user.cnic}</td>
              <td>
                <button onClick={() => handleEdit(user)}>Edit</button>
                <button onClick={() => handleDelete(user.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {editMode && (
        <div>
          <h2>Edit User</h2>
          <form onSubmit={() => handleSaveEdit(editedUser)}>
            <input
              type="text"
              name="firstName"
              value={editedUser.firstName}
              onChange={(e) => setEditedUser({ ...editedUser, firstName: e.target.value })}
              placeholder="First Name"
            />
            <input
              type="text"
              name="lastName"
              value={editedUser.lastName}
              onChange={(e) => setEditedUser({ ...editedUser, lastName: e.target.value })}
              placeholder="Last Name"
            />
            <input
              type="number"
              name="age"
              value={editedUser.age}
              onChange={(e) => setEditedUser({ ...editedUser, age: e.target.value })}
              placeholder="Age"
            />
            <input
              type="text"
              name="cnic"
              value={editedUser.cnic}
              onChange={(e) => setEditedUser({ ...editedUser, cnic: e.target.value })}
              placeholder="CNIC (xxxxx-xxxxxxx-x)"
            />
            <button type="submit">Save</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default UserTable;
